import React, { useState, useEffect } from 'react';
import { Route, Switch } from 'react-router-dom';
import axios from 'axios';
import Navbar from './components/Navbar';
import EmployeeList from './components/EmployeeList';
import AddEmployee from './components/AddEmployee';
import ViewEmployee from './components/ViewEmployee';
import UpdateEmployee from './components/UpdateEmployee';
import Login from './components/Login';
import Signup from './components/Signup';

// Define your backend API URL
const API_URL = 'http://localhost:3001';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Check if a token exists in localStorage to set authentication state
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
    }
  }, []);

  return (
    <div>
      <Navbar isAuthenticated={isAuthenticated} setIsAuthenticated={setIsAuthenticated} />
      <div className="container mt-5">
        <Switch>
          <Route exact path="/" component={Login} />
          <Route exact path="/signup" component={Signup} />
          <Route exact path="/employees" component={EmployeeList} />
          <Route exact path="/employees/add" component={AddEmployee} />
          <Route exact path="/employees/view/:id" component={ViewEmployee} />
          <Route exact path="/employees/update/:id" component={UpdateEmployee} />
        </Switch>
      </div>
    </div>
  );
}

export default App;
