import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]); // State to store employee data
  const [error, setError] = useState(null); // State to handle errors
  const [loading, setLoading] = useState(true); // State to show loading indicator

  // Fetch employees from API
  useEffect(() => {
    axios
      .get('http://localhost:3001/employee') // Replace with your actual backend URL
      .then((response) => {
        setEmployees(response.data); // Set employees state with the response data
        setLoading(false); // Stop loading
      })
      .catch((error) => {
        console.error('Error fetching employees:', error); // Log error
        setError('Failed to load employees. Please try again later.');
        setLoading(false); // Stop loading
      });
  }, []);

  // Render loading state
  if (loading) {
    return <div>Loading...</div>;
  }

  // Render error state
  if (error) {
    return <div style={{ color: 'red' }}>{error}</div>;
  }

  // Render employee list
  return (
    <div className="container">
      <h1>Employee List</h1>
      {employees.length === 0 ? (
        <p>No employees found.</p>
      ) : (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Email</th>
              <th>Position</th>
              <th>Salary</th>
              <th>Date of Joining</th>
              <th>Department</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((employee) => (
              <tr key={employee._id}>
                <td>{employee.first_name}</td>
                <td>{employee.last_name}</td>
                <td>{employee.email}</td>
                <td>{employee.position}</td>
                <td>${employee.salary}</td>
                <td>{new Date(employee.date_of_joining).toLocaleDateString()}</td>
                <td>{employee.department}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default EmployeeList;
